mapper.insert:

	SELECT
	CASE WHEN max(board_no) IS NULL
	THEN 1
	ELSE max(board_no) + 1
	END AS no
	FROM board


INSERT INTO board
VALUES(#{boardNo}
,#{title}
,#{content}
,#{dt}
,#{likes}
,#{hit}
,#{category}
,#{groupNo}
,#{userNo})
,#{writer}

mapper.update:
update board
set title = #{title}
,content = #{content}
,dt = #{dt}
,category = #{category}
where board_no = #{boardNo}
