mapper.insert:

	SELECT 
	    CASE WHEN max(comment_no) IS NULL 
	    THEN 1
	    ELSE max(comment_no) + 1 
	    END AS no
	FROM comments


INSERT INTO comments
VALUES(#{commentNo}
		,#{content}
		,sysdate
		,#{boardNo}
		,#{userNo}
		,#{nickname})


	SELECT
	CASE WHEN max(board_no) IS NULL
	THEN 1
	ELSE
	max(board_no) + 1
	END AS no
	FROM board


INSERT INTO board
VALUES(#{boardNo}
,#{title}
,#{content}
,#{dt}
,0
,0
,#{category}
,100
,#{userNo}
,#{nickname}
,#{attech, jdbcType = VARCHAR}
,#{attechDir, jdbcType = VARCHAR})

mapper.update:
UPDATE BOARD
title = #{title}, content = #{content}, attech = #{attech}, attechDir = #{attechDir}, category = #{category}
where board_no = #{boardNo}
