mapper.select:
SELECT *
FROM calendar
WHERE calendar_no = #{calendarNo}
AND user_no = #{userNo}

mapper.update:
UPDATE calendar
SET title = #{title},
	content = #{content},
	start_dt = #{startDt},
	end_dt = #{endDt}
WHERE calendar_no = #{calendarNo}

mapper.delete:
DELETE
FROM calendar
WHERE calendar_no = #{calendarNo}
AND user_no = #{userNo}
