mapper.insert:

	SELECT
	CASE WHEN max(hospital_no) IS NULL
	THEN 1
	ELSE max(hospital_no) + 1
	END AS no
	FROM hospital


INSERT INTO hospital
VALUES(#{hospitalNo}
		,#{name}
		,#{address}
		,${call}
		,${vet}
		,#{workingHour}
		,#{info}
		,#{latitude}
		,#{longitude}
		,#{categoryId})

mapper.update:
update hospital
set name = #{name}
,address = #{address}
,call = #{call}
,vet = #{vet}
,working_hour = #{workingHour}
,info = #{info}
,latitude = #{latitude}
,longitude = #{longitude}
where hospital_no = #{hospitalNo}
