mapper.delete:
DELETE
FROM scrap
WHERE scrap_no = #{scrapNo}
AND user_no = #{userNo}

DELETE
FROM board
WHERE board_no = #{boardNo}
AND user_no = #{userNo}

DELETE
FROM comments
WHERE comment_no = #{commentNo}
AND user_no = #{userNo}

mapper.insert:

	SELECT
	CASE WHEN max(user_no) IS NULL
	THEN 1
	ELSE
	max(user_no) + 1
	END AS no
	FROM users


